import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const Transaction = () => {
  const { search } = useLocation();
  const [txnDetails, setTxnDetails] = useState(null);
  console.log("Transaction component mounted"); 

  useEffect(() => {
    const params = new URLSearchParams(search);

    const details = {
      status: params.get("status"),
      txnId: params.get("txnId"),
      amount: params.get("amount"),
      timestamp: params.get("timestamp"),
      custRefNo: params.get("custRefNo"), // ‚úÖ new param
    };

    setTxnDetails(details);
  }, [search]);

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>üßæ Transaction Status</h1>

      {txnDetails ? (
        <div style={{ marginTop: "2rem", fontSize: "18px" }}>
          <p><strong>Status:</strong> {txnDetails.status}</p>
          <p><strong>Transaction ID:</strong> {txnDetails.txnId}</p>
          <p><strong>Amount:</strong> ‚Çπ{txnDetails.amount}</p>
          <p><strong>Date/Time:</strong> {txnDetails.timestamp}</p>
          <p><strong>Customer Ref No:</strong> {txnDetails.custRefNo}</p>

          {txnDetails.status === "success" ? (
            <p style={{ color: "green", fontWeight: "bold" }}>‚úÖ Payment Successful</p>
          ) : (
            <p style={{ color: "red", fontWeight: "bold" }}>‚ùå Payment Failed</p>
          )}
        </div>
      ) : (
        <p>Loading transaction details...</p>
      )}
    </div>
  );
};

export default Transaction;
