import React, { useState, useEffect } from "react";
import QRCode from "qrcode.react"; // You must install this package
import { useLocation } from "react-router-dom";

const UPI_ID = "yourcompany@upi"; // Replace with your real UPI ID

const MainPage = () => {
  const location = useLocation();
  const [username, setUsername] = useState("User");
  const [amount, setAmount] = useState("");
  const [upiUrl, setUpiUrl] = useState("");

  // Get the username from login (if passed via state)
  useEffect(() => {
    if (location.state && location.state.username) {
      setUsername(location.state.username);
    }
  }, [location.state]);

  const handlePay = () => {
    if (!amount || isNaN(amount)) {
      alert("Please enter a valid amount.");
      return;
    }

    const upiString = `upi://pay?pa=${UPI_ID}&pn=${encodeURIComponent(
      username
    )}&am=${amount}&cu=INR`;

    setUpiUrl(upiString);
  };

  return (
    <div style={{ padding: "2rem", textAlign: "center" }}>
      <h1>ðŸŽ‰ Welcome, {username}!</h1>
      <p>Youâ€™ve successfully logged in.</p>

      <div style={{ marginTop: "2rem" }}>
        <input
          type="number"
          placeholder="Enter amount (INR)"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          style={{
            padding: "10px",
            fontSize: "16px",
            width: "200px",
            marginRight: "10px",
          }}
        />
        <button
          onClick={handlePay}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            backgroundColor: "#007bff",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Generate QR
        </button>
      </div>

      {upiUrl && (
        <div style={{ marginTop: "2rem" }}>
          <h3>Scan this QR to Pay</h3>
          <QRCode value={upiUrl} size={200} />
          <p style={{ marginTop: "10px" }}>{upiUrl}</p>
        </div>
      )}
    </div>
  );
};

export default MainPage;
